import { Linking, TouchableOpacity } from 'react-native'
import { Box, Image, Text, HStack, ScrollView, VStack } from 'native-base'
import { MaterialCommunityIcons } from '@expo/vector-icons'

// Dados dos Reels (links das miniaturas + URLs do Instagram)
const REELS = [
  {
    id: 1,
    thumb:
      'data:image/jpeg;base64,', // GIF ou imagem estática
    url: 'https://www.instagram.com/rahdarcb/reel/DJRVsyMRq5T/',
  },
  {
    id: 2,
    thumb:
      'data:image/jpeg;base64,', // GIF ou imagem estática
    url: 'https://www.instagram.com/douglassomar/p/DI18IDBP2Tq/',
  },

  {
    id: 3,
    thumb:
      'data:image/jpeg;base64,', // GIF ou imagem estática
    url: 'https://www.instagram.com/douglassomar/p/DI18IDBP2Tq/',
  },
  // Adicione mais...
]

export function InstagramReelsCarousel() {
  const openReel = (url: string) => {
    Linking.openURL(url).catch(() =>
      alert(
        'Não foi possível abrir o Instagram. Verifique se o app está instalado.',
      ),
    )
  }

  return (
    <Box mt={4}>
      <VStack mb={2}>
        <HStack
          justifyContent="space-between"
          alignItems="center"
          mx={4}
          mb={1}
        >
          <Text fontSize="md" color="black.200" fontWeight="semibold">
            Conheça nossa loja!
          </Text>

          <TouchableOpacity
            onPress={() => openReel('https://www.instagram.com/rahdarcb/')}
          >
            <Box
              borderBottomWidth={3}
              borderColor="yellow.300"
              borderRadius="md"
              px={1}
            >
              <Text fontSize="sm" color="green.700" fontWeight="semibold">
                Ver todos
              </Text>
            </Box>
          </TouchableOpacity>
        </HStack>
        <Box mb={1} ml={4} width={24} height={1} bg="yellow.300" />
      </VStack>

      <ScrollView horizontal showsHorizontalScrollIndicator={false}>
        <HStack space={3} px={4}>
          {REELS.map((reel) => (
            <TouchableOpacity
              key={reel.id}
              onPress={() => openReel(reel.url)}
              activeOpacity={0.7}
            >
              <Box position="relative">
                {/* Miniatura (GIF ou imagem) */}
                <Image
                  source={{ uri: reel.thumb }}
                  alt={`Reel ${reel.id}`}
                  width={160}
                  height={240}
                  borderRadius="md"
                  resizeMode="cover"
                />
                {/* Ícone do Instagram sobreposto */}
                <Box
                  position="absolute"
                  bottom={2}
                  right={2}
                  bg="rgba(0,0,0,0.6)"
                  p={1}
                  borderRadius="full"
                >
                  <MaterialCommunityIcons
                    name="instagram"
                    size={24}
                    color="#E1306C"
                  />
                </Box>
              </Box>
            </TouchableOpacity>
          ))}
        </HStack>
      </ScrollView>
    </Box>
  )
}
